    def main_game(self):
        screen.fill((28, 155, 79))

        self.dt = time() - self.lastTime
        self.dt *= 60
        self.lastTime = time()

        for event in py.event.get():
            if event.type == py.QUIT:
                py.quit()
                sys.exit()

        screen.blit(self.street, (0, 0))
        if self.active or self.pause:
            #self.draw_lines()
            if self.ideal:
                self.draw_car(self.posXIdeal, self.posYIdeal)
            else:
                self.draw_car(self.posXAccident, self.posYAccident)
        
        screen.blit(self.details, (0, 0))
        
        if self.active or self.pause:
            self.draw_data(self.actualPosition)

        for cloud in self.clouds:
            cloud.move(screen, self.dt)
        
        if not self.active:
            if self.buttonStart.draw(screen):
                self.ideal = True
                self.active = True
                self.actualPosition = 0
            if self.buttonAccident.draw(screen):
                self.ideal = False
                self.active = True
                self.actualPosition = 0

    def draw_lines(self, posX, posY):
        lineColor = "#4E5258"
        xActual = 0
        for i in range(len(posY)-2):
            if xActual < len(posX)-2:
                xActual += 1
            
            print(posX)
            localX = posX[xActual] + (screenWidth/2) - self.xFix
            localY = posY[i] + self.yFix

            nextX = posX[xActual+1] + (screenWidth/2) - self.xFix
            nextY = posY[i+1] + self.yFix
            
            py.draw.line(screen, (255, 0, 0), (localX, localY), (nextX, nextY), 6)

    def draw_car(self, posX, posY):
        localX = (posX[self.actualPosition] + (screenWidth/2) - self.xFix)
        localY = posY[self.actualPosition] + self.yFix
        
        self.carRect = self.car.get_rect()
        self.carRect.center = (localX, localY)

        screen.blit(self.car, self.carRect)

        if self.ideal:
            if self.actualPosition <= len(posY) - 5:
                m = (posY[self.actualPosition+1]-posY[self.actualPosition])/(posX[self.actualPosition+1] - posX[self.actualPosition])
                angle = degrees(atan(m))

                self.angle = 180 - angle + 75.1608 + 15
                self.car = py.transform.rotate(self.carDefault, self.angle)
                self.actualPosition += 4
            else:
                self.active = False
                self.pause = True
        else:
            if self.actualPosition <= len(posY) - 7:
                m = (posY[self.actualPosition+1]-posY[self.actualPosition])/(posX[self.actualPosition+1] - posX[self.actualPosition])
                angle = degrees(atan(m))

                self.angle = 180 - angle + 75.1608 + 15
                self.car = py.transform.rotate(self.carDefault, self.angle)
                self.actualPosition += 6
            else:
                self.active = False
                self.pause = True

